number_charspeed.script:
local nodeW;
function onInit()
	nodeW = window.getDatabaseNode();
	local nodeCT = ActorManager.getCTNode(nodeW);
	if not nodeCT then setTooltipText("") end
end
function onHover(state)
	if state then
		local nodeCT = ActorManager.getCTNode(nodeW);
		if nodeCT then
			setTooltipText(Interface.getString("tooltip_dclick_swindow"))
		else
			setTooltipText("");
		end
	end
end
function onDoubleClick()
	local nodeCT = ActorManager.getCTNode(nodeW);
	if nodeCT then
		local wSpeed = SpeedManager.openSpeedWindow(nodeCT);
	end
	return true;
end

string_content_column.script:
local nodeW, nodePath, bOnCT;
function onInit()
	nodeW = window.getDatabaseNode();
	nodePath = DB.getPath(nodeW);
	bOnCT = string.match(nodePath, 'combattracker');
	if not bOnCT then setTooltipText("") end
end
function onDoubleClick()
	if bOnCT then SpeedManager.openSpeedWindow(nodeW) end
	return true;
end

local nodeW, nodePath, bOnCT;
function onInit()
	nodeW = window.getDatabaseNode();
	nodePath = DB.getPath(nodeW);
	bOnCT = string.match(nodePath, 'combattracker');
	if not bOnCT then setTooltipText("") end
end
function onDoubleClick()
	if bOnCT then SpeedManager.openSpeedWindow(nodeW) end
	return true;
end

number_static.script:
function onInit()
	update();
end
function onValueChanged()
	update();
end
function update()
	local nodeChar = window.getDatabaseNode();
	local nodeCharWtW = DB.getChild(nodeChar, 'WalkThisWay');
	local nBonusSpeed = DB.getValue(nodeCharWtW, 'bonus');
	local nodeWTW = DB.createNode('WalkThisWay');
	local nodeFrogToes = DB.getChild(nodeWTW, 'frogtoes')
	local nFrogToes;
	if nodeFrogToes then
		nFrogToes = DB.getValue(nodeFrogToes);
	end
	local bHasMNM = WtWCommon.hasExtension('MNM Character Sheet Effects Display');
	if ((nFrogToes and nFrogToes == 1) or bHasMNM) and nBonusSpeed then
		if nBonusSpeed > 0 then
			setFrame("headerpoweratwill")
			setVisible(true)
		elseif nBonusSpeed < 0 then
			setFrame("headerpowerenc")
			setVisible(true)
		else
			setVisible(false)
		end
	end
end

stringcontrol.script:
function onInit()
	setVisible(false)
end
function refresh()
	setVisible(false)
end
