number_static.script:
function onInit()
	if super and super.onInit then super.onInit() end

	update();
end
function onValueChanged()
	update();
end
function update()
	local nodeChar = window.getDatabaseNode();
	local nodeCharWtW = DB.getChild(nodeChar, 'WalkThisWay');
	local nBonusSpeed = DB.getValue(nodeCharWtW, 'bonus');
	local nodeWTW = DB.createNode('WalkThisWay');
	if nBonusSpeed then
		if nBonusSpeed > 0 then
			setFrame("leathergreen")
			setVisible(true)
		elseif nBonusSpeed < 0 then
			setFrame("leatherred")
			setVisible(true)
		else
			setVisible(false)
		end
	end
end

stringcontrol.script:
function onInit()
	setVisible(false);
end
function refresh()
	setVisible(false);
end

number_charspeed.script:
local nodeW;
function onInit()
	--if super and super.onInit then super.onInit() end
		--this for some reason makes it show 0 when first opening

	if WtWCommon.hasExtension('MNM Character Sheet Tweaks') then
		setVisible(false);
		setEnabled(false);
	else
		nodeW = window.getDatabaseNode();
		local nodeCT = ActorManager.getCTNode(nodeW);
		if not nodeCT then setTooltipText("") end
	end
end
function onHover(state)
	if state then
		local nodeCT = ActorManager.getCTNode(nodeW);
		if nodeCT then
			setTooltipText(Interface.getString("tooltip_dclick_swindow"))
		else
			setTooltipText("");
		end
	end
end
function onDoubleClick()
	local nodeCT = ActorManager.getCTNode(nodeW);
	if nodeCT then
		local wSpeed = SpeedManager.openSpeedWindow(nodeCT);
	end
	return true;
end
